import React, { useEffect, useState } from "react";
import { Button, Form, Input, Table, Popconfirm, Modal } from "antd";
import "./supplier.css";
import { getSupplier } from "../../api/index.js";
import { useForm } from "antd/es/form/Form.js";

const Supplier = () => {
  const [listData, setListData] = useState({ name: "" });
  const [tableData, setTableData] = useState([]);
  // 0 add, 1 edit
  const [modalType, setModalType] = useState(0);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [form] = Form.useForm;

  const getTableData = () => {
    getSupplier(listData)
      .then(({ data }) => {
        console.log(data.data);
        setTableData(data.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  useEffect(() => {
    getTableData();
  }, []);

  // Implement adding or editing a supplier
  const handleClick = (type, rowData) => {
    setIsModalOpen(!isModalOpen);
    if (type === "add") {
      setModalType(0);
    } else {
      setModalType(1);
    }
    // console.log(type, rowData);
  };

  // confirm Modal
  const handleOk = () => {};

  // cancel Modal
  const handleCancel = () => {
    setIsModalOpen(false);
  };

  // handle form submission
  const handleFinisch = (e) => {
    setListData({
      name: e.name,
    });
    console.log(e);
  };

  // Implement Delete
  const handleDelete = (id) => {};

  const columns = [
    {
      title: "ID",
      dataIndex: "id",
    },
    {
      title: "supplier",
      dataIndex: "supplierName",
    },
    {
      title: "Address",
      dataIndex: "address",
    },
    {
      title: "Contact Person",
      dataIndex: "contactPerson",
    },
    {
      title: "E-Mail",
      dataIndex: "email",
    },
    {
      title: "Comment",
      dataIndex: "comment",
    },
    {
      title: "Create Time",
      dataIndex: "createTime",
    },
    {
      title: "Action",
      render: (rowData) => {
        return (
          <div className="flex-box">
            <Button
              style={{ marginRight: "5px" }}
              onClick={() => handleClick("edit", rowData)}
            >
              Edit
            </Button>
            <Popconfirm
              title="Do you really want to delete this supplier?"
              okText="Confirm"
              cancelText="Cancel"
              onConfirm={() => handleDelete(rowData.id)}
            >
              <Button type="primary" danger>
                Delete
              </Button>
            </Popconfirm>
          </div>
        );
      },
    },
  ];

  return (
    <div className="supplier">
      <div className="flex-box space-between">
        <Button
          type="primary"
          onClick={() => {
            handleClick("add");
          }}
        >
          +Add Supplier
        </Button>
        <Form layout="inline" onFinish={handleFinisch}>
          <Form.Item name="keyword">
            <Input placeholder="Please enter supplier name"></Input>
          </Form.Item>
          <Form.Item>
            <Button htmlType="submit" type="primary">
              Search
            </Button>
          </Form.Item>
        </Form>
      </div>
      <Table columns={columns} dataSource={tableData} rowKey={"id"}></Table>
      <Modal
        open={isModalOpen}
        title={modalType ? "Edit Supplier" : "Add Supplier"}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form
          form={form}
          labelCol={{
            span: 6,
          }}
          wrapperCol={{
            span: 18,
          }}
          labelAlign="left"
        ></Form>
      </Modal>
    </div>
  );
};

export default Supplier;
