import http from "./axios";

//Permission Management
export const getPermission = (user) => {
  return http.request({
    url: `/login`,
    method: "post",
    data: user, // Ensure user data is sent in the request body
  });
};

// export const getData = () => {
//   return http.request({
//     url: "/home/getData",
//     method: "get",
//   });
// };

//Supplier Management
export const getSupplier = (params) => {
  return http.request({
    url: "/supplier/getSupplier",
    method: "get",
    params,
  });
};

export const addSupplier = (data) => {
  return http.request({
    url: "/supplier/addSupplier",
    method: "post",
    data,
  });
};

export const updateSupplier = (data) => {
  return http.request({
    url: "/supplier/updateSupplier",
    method: "put",
    data,
  });
};

export const delSupplier = (id) => {
  return http.request({
    url: `/supplier/delSupplier/${id}`,
    method: "delete",
  });
};

//Order Management

export const getOrder = (params) => {
  return http.request({
    url: "/order/getOrder",
    method: "get",
    params,
  });
};

export const addOrder = (data) => {
  return http.request({
    url: "/order/addOrder",
    method: "post",
    data,
  });
};

export const updateOrder = (data) => {
  return http.request({
    url: "/order/updateOrder",
    method: "put",
    data,
  });
};

export const delOrder = (id) => {
  return http.request({
    url: `/order/delOrder/${id}`,
    method: "delete",
  });
};
