import React, { useEffect, useState } from "react";
import { Button, Form, Input, Table, Popconfirm, Modal } from "antd";
import "./supplier.css";
import {
  getSupplier,
  addSupplier,
  updateSupplier,
  delSupplier,
} from "../../api/index.js";

const Supplier = () => {
  const [listData, setListData] = useState({
    supplierName: "",
    page: 1,
    pageSize: 10,
  });
  const [tableData, setTableData] = useState([]);
  const [modalType, setModalType] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [form] = Form.useForm();

  const handleFinish = (values) => {
    setListData({ ...listData, supplierName: values.supplierName });
  };

  const handlePageChange = (page, pageSize) => {
    setListData({ ...listData, page: page });
  };

  const getTableData = () => {
    getSupplier(listData)
      .then(({ data }) => {
        setTableData(data.data.row);
      })
      .catch((error) => {
        console.error("Error fetching suppliers:", error);
      });
  };

  const handleClick = (type, rowData) => {
    setIsModalOpen(!isModalOpen);
    if (type === "add") {
      setModalType(0);
    } else {
      setModalType(1);
      const cloneData = { ...rowData }; // Deep clone rowData
      form.setFieldsValue(cloneData);
    }
  };

  const handleOk = () => {
    form
      .validateFields()
      .then((values) => {
        if (modalType) {
          updateSupplier(values).then(() => {
            handleCancel();
            getTableData();
          });
        } else {
          addSupplier(values).then(() => {
            handleCancel();
            getTableData();
          });
        }
      })
      .catch((err) => {
        console.error("Validation failed:", err);
      });
  };

  const handleCancel = () => {
    setIsModalOpen(false);
    form.resetFields();
  };

  const handleDelete = (id) => {
    delSupplier(id)
      .then(() => {
        getTableData();
      })
      .catch((error) => {
        console.error("Error deleting supplier:", error);
      });
  };

  useEffect(() => {
    getTableData();
  }, [listData]);

  const columns = [
    {
      title: "ID",
      dataIndex: "id",
    },
    {
      title: "Supplier",
      dataIndex: "supplierName",
    },
    {
      title: "Address",
      dataIndex: "address",
    },
    {
      title: "Contact Person",
      dataIndex: "contactPerson",
    },
    {
      title: "E-Mail",
      dataIndex: "email",
    },
    {
      title: "Comment",
      dataIndex: "comment",
    },
    {
      title: "Action",
      render: (rowData) => (
        <div className="flex-box">
          <Button
            style={{ marginRight: "5px" }}
            onClick={() => handleClick("edit", rowData)}
          >
            Edit
          </Button>
          <Popconfirm
            title="Do you really want to delete this supplier?"
            okText="Confirm"
            cancelText="Cancel"
            onConfirm={() => handleDelete(rowData.id)}
          >
            <Button type="primary" danger>
              Delete
            </Button>
          </Popconfirm>
        </div>
      ),
    },
  ];

  return (
    <div className="supplier">
      <div className="flex-box space-between">
        <Form layout="inline" onFinish={handleFinish}>
          <Form.Item name="supplierName">
            <Input placeholder="Supplier name"></Input>
          </Form.Item>
          <Form.Item>
            <Button htmlType="submit" type="primary">
              Search
            </Button>
          </Form.Item>
        </Form>
        <Button type="primary" onClick={() => handleClick("add")}>
          +Add Supplier
        </Button>
      </div>
      <Table
        style={{ marginTop: "20px" }}
        columns={columns}
        dataSource={tableData}
        rowKey={"id"}
        pagination={{
          pageSize: 10,
          onChange: handlePageChange,
        }}
      ></Table>
      <Modal
        visible={isModalOpen}
        title={modalType ? "Edit Supplier" : "Add Supplier"}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form
          form={form}
          labelCol={{ span: 6 }}
          wrapperCol={{ span: 18 }}
          labelAlign="left"
        >
          {modalType === 1 && (
            <Form.Item name="id" hidden>
              <Input />
            </Form.Item>
          )}
          <Form.Item
            label="Supplier"
            name="supplierName"
            rules={[{ required: true, message: "Please enter supplier name" }]}
          >
            <Input placeholder="Enter supplier name"></Input>
          </Form.Item>
          <Form.Item
            label="Address"
            name="address"
            rules={[
              { required: true, message: "Please enter supplier address" },
            ]}
          >
            <Input placeholder="Enter supplier address"></Input>
          </Form.Item>
          <Form.Item
            label="Contact Person"
            name="contactPerson"
            rules={[{ required: true, message: "Please enter contact person" }]}
          >
            <Input placeholder="Enter contact person"></Input>
          </Form.Item>
          <Form.Item
            label="E-Mail"
            name="email"
            rules={[{ required: true, message: "Please enter email" }]}
          >
            <Input placeholder="Enter email"></Input>
          </Form.Item>
          <Form.Item
            label="Comment"
            name="comment"
            rules={[{ required: false }]}
          >
            <Input placeholder="Enter comment"></Input>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default Supplier;
