import React from "react";
import { CheckCircleOutlined } from "@ant-design/icons";
import * as Icons from "@ant-design/icons";
import { Card, Col } from "antd";
import "./home.css";

import { getPermission } from "../../api";

const iconToElement = (name) => React.createElement(Icons[name]);

const Home = () => {
  const [supplierCount, setSupplierCount] = useState(0);

  useEffect(() => {
    countSupplier()
      .then((response) => {
        setSupplierCount(response.data);
      })
      .catch((error) => {
        console.error("Error fetching supplier count:", error);
      });
  }, []);

  const countData = [
    {
      name: "New Supplier of this Month",
      value: supplierCount,
      icon: "CheckCircleOutlined",
      color: "#728FCE",
    },
    {
      name: "Total Supplier Amount",
      value: 12,
      icon: "CheckCircleOutlined",
      color: "#728FCE",
    },
    {
      name: "New Purchase Order of this Month",
      value: 12,
      icon: "CheckCircleOutlined",
      color: "#77BFC7",
    },
    {
      name: "Total Purchase Amount of this Month",
      value: 12,
      icon: "CheckCircleOutlined",
      color: "#77BFC7",
    },
  ];

  return (
    <Col span={15}>
      <div className="num">
        {countData.map((item, index) => {
          return (
            <Card hoverable key={index}>
              <div className="icon-box" style={{ background: item.color }}>
                {iconToElement(item.icon)}
              </div>
              <div className="detail">
                <p className="num">{item.value}</p>
                <p className="txt">{item.name}</p>
              </div>
            </Card>
          );
        })}
      </div>
    </Col>
  );
};

export default Home;
