import React, { useEffect, useState } from "react";
import { Button, Form, Input, Table, Popconfirm, Modal } from "antd";
import "./supplier.css";
import {
  getSupplier,
  addSupplier,
  updateSupplier,
  delSupplier,
} from "../../api/index.js";
const Supplier = () => {
  const [listData, setListData] = useState({ keyword: "" });
  const [tableData, setTableData] = useState([]);
  const [modalType, setModalType] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [form] = Form.useForm();

  const handleClick = (type, rowData) => {
    setIsModalOpen(true);
    if (type === "add") {
      setModalType(0);
    } else {
      setModalType(1);
      const cloneData = JSON.parse(JSON.stringify(rowData));
      form.setFieldsValue(cloneData);
    }
  };

  const handleOk = () => {
    form
      .validateFields()
      .then((val) => {
        if (modalType) {
          updateSupplier(val)
            .then(() => {
              handleCancel();
              getTableData();
            })
            .catch((error) => {
              message.error("Failed to update supplier");
              console.error("Update error:", error);
            });
        } else {
          addSupplier(val)
            .then(() => {
              handleCancel();
              getTableData();
            })
            .catch((error) => {
              message.error("Failed to add supplier");
              console.error("Add error:", error);
            });
        }
      })
      .catch((err) => {
        console.log("Validation error:", err);
      });
  };

  const handleCancel = () => {
    setIsModalOpen(false);
    form.resetFields();
  };

  const handleFinish = (e) => {
    setListData({ keyword: e.keyword });
  };

  useEffect(() => {
    getTableData();
  }, [listData]);

  const handleDelete = (id) => {
    delSupplier(id)
      .then(() => {
        getTableData();
      })
      .catch((error) => {
        message.error("Failed to delete supplier");
        console.error("Delete error:", error);
      });
  };

  const getTableData = () => {
    getSupplier(listData)
      .then(({ data }) => {
        if (data.code === 1) {
          setTableData(data.data);
        } else {
          message.error(data.msg);
        }
      })
      .catch((error) => {
        message.error("Failed to fetch suppliers");
        console.error("Fetch error:", error);
      });
  };

  const columns = [
    {
      title: "ID",
      dataIndex: "id",
    },
    {
      title: "Supplier",
      dataIndex: "supplierName",
    },
    {
      title: "Address",
      dataIndex: "address",
    },
    {
      title: "Contact Person",
      dataIndex: "contactPerson",
    },
    {
      title: "E-Mail",
      dataIndex: "email",
    },
    {
      title: "Comment",
      dataIndex: "comment",
    },
    {
      title: "Action",
      render: (rowData) => {
        return (
          <div className="flex-box">
            <Button
              style={{ marginRight: "5px" }}
              onClick={() => handleClick("edit", rowData)}
            >
              Edit
            </Button>
            <Popconfirm
              title="Do you really want to delete this supplier?"
              okText="Confirm"
              cancelText="Cancel"
              onConfirm={() => handleDelete(rowData.id)}
            >
              <Button type="primary" danger>
                Delete
              </Button>
            </Popconfirm>
          </div>
        );
      },
    },
  ];

  return (
    <div className="supplier">
      <div className="flex-box space-between">
        <Form layout="inline" onFinish={handleFinish}>
          <Form.Item name="keyword">
            <Input placeholder="supplier name"></Input>
          </Form.Item>
          <Form.Item>
            <Button htmlType="submit" type="primary">
              Search
            </Button>
          </Form.Item>
        </Form>
        <Button
          type="primary"
          onClick={() => {
            handleClick("add");
          }}
        >
          +Add Supplier
        </Button>
      </div>
      <Table
        style={{ marginTop: "20px" }}
        columns={columns}
        dataSource={tableData}
        rowKey={"id"}
      />
      <Modal
        open={isModalOpen}
        title={modalType ? "Edit Supplier" : "Add Supplier"}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form
          form={form}
          labelCol={{
            span: 6,
          }}
          wrapperCol={{
            span: 18,
          }}
          labelAlign="left"
        >
          {modalType === 1 && (
            <Form.Item name="id" hidden>
              <Input />
            </Form.Item>
          )}
          <Form.Item
            label="Supplier"
            name="supplierName"
            rules={[
              {
                required: true,
                message: "Please enter supplier name",
              },
            ]}
          >
            <Input placeholder="Please enter supplier name" />
          </Form.Item>
          <Form.Item
            label="Address"
            name="address"
            rules={[
              {
                required: true,
                message: "Please enter supplier address",
              },
            ]}
          >
            <Input placeholder="Please enter supplier address" />
          </Form.Item>
          <Form.Item
            label="Contact Person"
            name="contactPerson"
            rules={[
              {
                required: true,
                message: "Please enter supplier contact person",
              },
            ]}
          >
            <Input placeholder="Please enter supplier contact person" />
          </Form.Item>
          <Form.Item
            label="E-Mail"
            name="email"
            rules={[
              {
                required: true,
                message: "Please enter supplier E-Mail",
              },
            ]}
          >
            <Input placeholder="Please enter supplier E-Mail" />
          </Form.Item>
          <Form.Item
            label="Comment"
            name="comment"
            rules={[
              {
                required: false,
                message: "Enter comment if needed",
              },
            ]}
          >
            <Input placeholder="Enter comment if needed" />
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default Supplier;

