import React, { useEffect, useState } from "react";
import { Button, Form, Input, Table, Popconfirm, Modal, message } from "antd";
import "./order.css";
import { getOrder, addOrder, updateOrder, delOrder } from "../../api/index.js";

const Order = () => {
  const [listData, setListData] = useState({ keyword: "" });
  const [tableData, setTableData] = useState([]);
  // 0 add, 1 edit
  const [modalType, setModalType] = useState(0);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [form] = Form.useForm();

  // Implement adding or editing a supplier
  const handleClick = (type, rowData) => {
    setIsModalOpen(!isModalOpen);
    if (type === "add") {
      setModalType(0);
    } else {
      setModalType(1);
      //get data from the row
      const cloneData = JSON.parse(JSON.stringify(rowData));
      form.setFieldsValue(cloneData);
    }
  };

  // confirm Modal
  const handleOk = () => {
    //validate form
    form
      .validateFields()
      .then((val) => {
        // console.log(val, "val");
        if (modalType) {
          updateOrder(val).then(() => {
            handleCancel();
            getTableData();
          });
        } else {
          addOrder(val).then(() => {
            //close the modal
            handleCancel();
            //update data in the table
            getTableData();
          });
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  // cancel Modal
  const handleCancel = () => {
    setIsModalOpen(false);
    //clear data in the form
    form.resetFields();
  };

  // handle search form submission
  const handleFinish = (e) => {
    setListData({ keyword: e.keyword }); //updates the state variable listData
    getOrder({ keyword: e.keyword }) //makes an API call to retrieve data from the backend
      .then(({ data }) => {
        setTableData(data.data); // Update table data with the filtered results
      })
      .catch((error) => {
        console.error("Error searching orders:", error);
      });
  };

  useEffect(() => {
    getTableData();
    console.log(tableData, "effect");
  }, [listData]);

  // Implement Delete
  const handleDelete = (id) => {
    delOrder(id)
      .then(() => {
        getTableData();
      })
      .catch((error) => {
        console.error("Error deleting order:", error);
      });
  };

  //get the table data
  const getTableData = () => {
    // console.log("Request parameters:", listData);
    getSupplier(listData)
      .then(({ data }) => {
        console.log(data.data, "res");
        setTableData(data.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const columns = [
    {
      title: "ID",
      dataIndex: "id",
    },
    {
      title: "Supplier",
      dataIndex: "supplierName",
    },
    {
      title: "Material Name",
      dataIndex: "amaterialName",
    },
    {
      title: "Material Quantity",
      dataIndex: "materialQuantity",
    },
    {
      title: "Single Price",
      dataIndex: "singlePrice",
    },
    {
      title: "Total Price",
      dataIndex: "totalPrice",
    },
    {
      title: "Action",
      render: (rowData) => {
        return (
          <div className="flex-box">
            <Button
              style={{ marginRight: "5px" }}
              onClick={() => handleClick("edit", rowData)}
            >
              Edit
            </Button>
            <Popconfirm
              title="Do you really want to delete this order?"
              okText="Confirm"
              cancelText="Cancel"
              onConfirm={() => handleDelete(rowData.id)}
            >
              <Button type="primary" danger>
                Delete
              </Button>
            </Popconfirm>
          </div>
        );
      },
    },
  ];

  return (
    <div className="order">
      <div className="flex-box space-between">
        <Form layout="inline" onFinish={handleFinish}>
          <Form.Item name="keyword">
            <Input placeholder="order name"></Input>
          </Form.Item>
          <Form.Item>
            <Button htmlType="submit" type="primary">
              Search
            </Button>
          </Form.Item>
        </Form>
        <Button
          type="primary"
          onClick={() => {
            handleClick("add");
          }}
        >
          +Add Supplier
        </Button>
      </div>
      <Table
        style={{ marginTop: "20px" }}
        columns={columns}
        dataSource={tableData}
        rowKey={"id"}
      ></Table>
      <Modal
        open={isModalOpen}
        title={modalType ? "Edit Order" : "Add Order"}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form
          form={form}
          labelCol={{
            span: 6,
          }}
          wrapperCol={{
            span: 18,
          }}
          labelAlign="left"
        >
          {modalType === 1 && (
            <Form.Item name="id" hidden>
              <Input />
            </Form.Item>
          )}

          <Form.Item
            label="Supplier"
            name="supplierName"
            rules={[
              {
                required: true,
                message: "Please enter supplier name",
              },
            ]}
          >
            <Input placeholder="Please enter supplier name"></Input>
          </Form.Item>
          <Form.Item
            label="MaterialName"
            name="address"
            rules={[
              {
                required: true,
                message: "Please enter supplier address",
              },
            ]}
          >
            <Input placeholder="PPlease enter supplier address"></Input>
          </Form.Item>
          <Form.Item
            label="Contact Person"
            name="contactPerson"
            rules={[
              {
                required: true,
                message: "Please enter supplier contact person",
              },
            ]}
          >
            <Input placeholder="Please enter supplier contact person"></Input>
          </Form.Item>
          <Form.Item
            label="E-Mail"
            name="email"
            rules={[
              {
                required: true,
                message: "Please enter supplier E-Mail",
              },
            ]}
          >
            <Input placeholder="Please enter supplier E-Mail"></Input>
          </Form.Item>
          <Form.Item
            label="Comment"
            name="comment"
            rules={[
              {
                required: false,
                message: "Enter comment if needed",
              },
            ]}
          >
            <Input placeholder="Enter comment if needed"></Input>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default Supplier;
