import React, { useEffect, useState } from "react";
import { Button, Form, Input, Table, Popconfirm, Modal, message } from "antd";
import "./supplier.css";
import {
  getSupplier,
  addSupplier,
  updateSupplier,
  delSupplier,
} from "../../api/index.js";

const Supplier = () => {
  const [listData, setListData] = useState({
    supplierName: "",
    page: 1,
    pageSize: 10,
  });
  const [tableData, setTableData] = useState([]);
  // 0 add, 1 edit
  const [modalType, setModalType] = useState(0);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [form] = Form.useForm();

  // Implement adding or editing a supplier
  const handleClick = (type, rowData) => {
    setIsModalOpen(!isModalOpen);
    if (type === "add") {
      setModalType(0);
    } else {
      setModalType(1);
      //get data from the row
      const cloneData = JSON.parse(JSON.stringify(rowData));
      form.setFieldsValue(cloneData);
    }
  };

  // confirm Modal
  const handleOk = () => {
    //validate form
    form
      .validateFields()
      .then((values) => {
        // console.log(val, "val");
        if (modalType) {
          updateSupplier(values).then(() => {
            handleCancel();
            getTableData();
          });
        } else {
          addSupplier(values).then(() => {
            //close the modal
            handleCancel();
            //update data in the table
            getTableData();
          });
        }
      })
      .catch((err) => {
        console.error("Validation failed:", err);
      });
  };

  // cancel Modal
  const handleCancel = () => {
    setIsModalOpen(false);
    //clear data in the form
    form.resetFields();
  };

  // handle search form submission
  const handleFinish = (values) => {
    setListData({ ...listData, supplierName: values.supplierName });
  };

  // const handleFinish = (e) => {
  //   setListData({ keyword: e.keyword }); //updates the state variable listData
  //   getSupplier({ keyword: e.keyword }) //makes an API call to retrieve data from the backend
  //     .then(({ data }) => {
  //       setTableData(data.data); // Update table data with the filtered results
  //     })
  //     .catch((error) => {
  //       console.error("Error searching suppliers:", error);
  //     });
  // };

  useEffect(() => {
    getTableData();
    console.log(tableData, "effect");
  }, [listData]);

  // Implement Delete
  const handleDelete = (id) => {
    delSupplier(id)
      .then(() => {
        getTableData();
      })
      .catch((error) => {
        console.error("Error deleting supplier:", error);
      });
  };

  const handlePageChange = (page, pageSize) => {
    setListData({ ...listData, page: page });
  };

  // const handlePageChange = (page, pageSize) => {
  //   // Make API call with updated page number
  //   getSupplier({ keyword: listData.keyword, page: page })
  //     .then(({ data }) => {
  //       setTableData(data.data); // Update table data with new page data
  //     })
  //     .catch((error) => {
  //       console.error("Error fetching suppliers:", error);
  //     });
  // };

  //get the table data
  const getTableData = () => {
    getSupplier(listData)
      .then(({ data }) => {
        // console.log(data.data, "res");
        setTableData(data.data);
      })
      .catch((error) => {
        console.error("Error fetching suppliers:", error);
      });
  };

  const columns = [
    {
      title: "ID",
      dataIndex: "id",
    },
    {
      title: "Supplier",
      dataIndex: "supplierName",
    },
    {
      title: "Address",
      dataIndex: "address",
    },
    {
      title: "Contact Person",
      dataIndex: "contactPerson",
    },
    {
      title: "E-Mail",
      dataIndex: "email",
    },
    {
      title: "Comment",
      dataIndex: "comment",
    },
    {
      title: "Action",
      render: (rowData) => {
        return (
          <div className="flex-box">
            <Button
              style={{ marginRight: "5px" }}
              onClick={() => handleClick("edit", rowData)}
            >
              Edit
            </Button>
            <Popconfirm
              title="Do you really want to delete this supplier?"
              okText="Confirm"
              cancelText="Cancel"
              onConfirm={() => handleDelete(rowData.id)}
            >
              <Button type="primary" danger>
                Delete
              </Button>
            </Popconfirm>
          </div>
        );
      },
    },
  ];

  return (
    <div className="supplier">
      <div className="flex-box space-between">
        <Form layout="inline" onFinish={handleFinish}>
          <Form.Item name="keyword">
            <Input placeholder="supplier name"></Input>
          </Form.Item>
          <Form.Item>
            <Button htmlType="submit" type="primary">
              Search
            </Button>
          </Form.Item>
        </Form>
        <Button
          type="primary"
          onClick={() => {
            handleClick("add");
          }}
        >
          +Add Supplier
        </Button>
      </div>
      <Table
        style={{ marginTop: "20px" }}
        columns={columns}
        dataSource={tableData}
        rowKey={"id"}
        pagination={{
          pageSize: 10, // Number of items per page
          onChange: (page, pageSize) => handlePageChange(page, pageSize),
        }}
      ></Table>
      <Modal
        open={isModalOpen}
        title={modalType ? "Edit Supplier" : "Add Supplier"}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form
          form={form}
          labelCol={{
            span: 6,
          }}
          wrapperCol={{
            span: 18,
          }}
          labelAlign="left"
        >
          {modalType === 1 && (
            <Form.Item name="id" hidden>
              <Input />
            </Form.Item>
          )}

          <Form.Item
            label="Supplier"
            name="supplierName"
            rules={[
              {
                required: true,
                message: "Please enter supplier name",
              },
            ]}
          >
            <Input placeholder="Please enter supplier name"></Input>
          </Form.Item>
          <Form.Item
            label="Address"
            name="address"
            rules={[
              {
                required: true,
                message: "Please enter supplier address",
              },
            ]}
          >
            <Input placeholder="PPlease enter supplier address"></Input>
          </Form.Item>
          <Form.Item
            label="Contact Person"
            name="contactPerson"
            rules={[
              {
                required: true,
                message: "Please enter supplier contact person",
              },
            ]}
          >
            <Input placeholder="Please enter supplier contact person"></Input>
          </Form.Item>
          <Form.Item
            label="E-Mail"
            name="email"
            rules={[
              {
                required: true,
                message: "Please enter supplier E-Mail",
              },
            ]}
          >
            <Input placeholder="Please enter supplier E-Mail"></Input>
          </Form.Item>
          <Form.Item
            label="Comment"
            name="comment"
            rules={[
              {
                required: false,
                message: "Enter comment if needed",
              },
            ]}
          >
            <Input placeholder="Enter comment if needed"></Input>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default Supplier;
