import React from "react";
import { Form, Input, Button, message } from "antd";
import "./login.css";
import { getPermission } from "../../api";
import { useNavigate, Navigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();
  //if logged in, naviagate to home
  if (localStorage.getItem("token")) {
    return <Navigate to="/home" replace />;
  }
  const handleSubmit = (val) => {
    if (!val.password || !val.username) {
      return message.open({
        type: "warning",
        content: "Please enter usename and password",
      });
    }
    getPermission(val).then(({ data }) => {
      console.log(data);
      localStorage.setItem("token", data.data.token);
      navigate("/home");
    });
  };

  return (
    <Form className="login-container" onFinish={handleSubmit}>
      <div className="login_title">System Login</div>
      <Form.Item label="Account Name" name="username">
        <Input placeholder="Please enter account name" />
      </Form.Item>

      <Form.Item label="Password" name="password">
        <Input.Password placeholder="Please enter password" />
      </Form.Item>
      <Form.Item className="login-button">
        <Button type="primary" htmlType="submit">
          Login
        </Button>
      </Form.Item>
    </Form>
  );
};
export default Login;
