import React, { useEffect, useState } from "react";
import { Button, Form, Input, Table, Popconfirm, Modal } from "antd";
import "./order.css";
import { getOrder, addOrder, updateOrder, delOrder } from "../../api/index.js";

const Order = () => {
  const [materialKeyword, setMaterialKeyword] = useState("");
  const [supplierKeyword, setSupplierKeyword] = useState("");
  const [tableData, setTableData] = useState([]);
  const [modalType, setModalType] = useState(0);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [form] = Form.useForm();

  const handleClick = (type, rowData) => {
    setIsModalOpen(!isModalOpen);
    if (type === "add") {
      setModalType(0);
    } else {
      setModalType(1);
      const cloneData = JSON.parse(JSON.stringify(rowData));
      form.setFieldsValue(cloneData);
    }
  };

  const handleOk = () => {
    form
      .validateFields()
      .then((val) => {
        if (modalType) {
          updateOrder(val).then(() => {
            handleCancel();
            getTableData();
          });
        } else {
          addOrder(val).then(() => {
            handleCancel();
            getTableData();
          });
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const handleCancel = () => {
    setIsModalOpen(false);
    form.resetFields();
  };

  const handleFinish = (values) => {
    const { materialName, supplierName } = values;
    setMaterialKeyword(materialName || ""); // Set material keyword, empty string if undefined
    setSupplierKeyword(supplierName || ""); // Set supplier keyword, empty string if undefined
  };

  useEffect(() => {
    getTableData();
  }, [materialKeyword, supplierKeyword]);

  const handleDelete = (id) => {
    delOrder(id)
      .then(() => {
        getTableData();
      })
      .catch((error) => {
        console.error("Error deleting order:", error);
      });
  };

  const getTableData = () => {
    getOrder({ materialName: materialKeyword, supplierName: supplierKeyword })
      .then(({ data }) => {
        setTableData(data.data);
      })
      .catch((error) => {
        console.error("Error fetching orders:", error);
      });
  };

  const columns = [
    { title: "ID", dataIndex: "id" },
    { title: "Supplier", dataIndex: "supplierName" },
    { title: "Material Name", dataIndex: "materialName" },
    { title: "Material Quantity", dataIndex: "materialQuantity" },
    { title: "Single Price", dataIndex: "singlePrice" },
    { title: "Total Price", dataIndex: "totalPrice" },
    {
      title: "Action",
      render: (rowData) => (
        <div className="flex-box">
          <Button
            style={{ marginRight: "5px" }}
            onClick={() => handleClick("edit", rowData)}
          >
            Edit
          </Button>
          <Popconfirm
            title="Do you really want to delete this order?"
            okText="Confirm"
            cancelText="Cancel"
            onConfirm={() => handleDelete(rowData.id)}
          >
            <Button type="primary" danger>
              Delete
            </Button>
          </Popconfirm>
        </div>
      ),
    },
  ];

  return (
    <div className="order">
      <div className="flex-box space-between">
        <Form layout="inline" onFinish={handleFinish}>
          <Form.Item name="materialName">
            <Input placeholder="Search by material name" />
          </Form.Item>
          <Form.Item name="supplierName">
            <Input placeholder="Search by supplier" />
          </Form.Item>
          <Form.Item>
            <Button htmlType="submit" type="primary">
              Search
            </Button>
          </Form.Item>
        </Form>
        <Button type="primary" onClick={() => handleClick("add")}>
          + Add Supplier
        </Button>
      </div>
      <Table
        style={{ marginTop: "20px" }}
        columns={columns}
        dataSource={tableData}
        rowKey={"id"}
      />
      <Modal
        visible={isModalOpen}
        title={modalType ? "Edit Order" : "Add Order"}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form
          form={form}
          labelCol={{ span: 6 }}
          wrapperCol={{ span: 18 }}
          labelAlign="left"
        >
          {modalType === 1 && (
            <Form.Item name="id" hidden>
              <Input />
            </Form.Item>
          )}
          <Form.Item
            label="Supplier"
            name="supplierName"
            rules={[{ required: true, message: "Please enter supplier name" }]}
          >
            <Input placeholder="Please enter supplier name" />
          </Form.Item>
          <Form.Item
            label="Material Name"
            name="materialName"
            rules={[{ required: true, message: "Please enter material name" }]}
          >
            <Input placeholder="Please enter material name" />
          </Form.Item>
          <Form.Item
            label="Material Quantity"
            name="materialQuantity"
            rules={[
              { required: true, message: "Please enter material quantity" },
            ]}
          >
            <Input placeholder="Please enter material quantity" />
          </Form.Item>
          <Form.Item
            label="Single Price"
            name="singlePrice"
            rules={[{ required: true, message: "Please enter single price" }]}
          >
            <Input placeholder="Please enter single price" />
          </Form.Item>
          <Form.Item
            label="Total Price"
            name="totalPrice"
            rules={[{ required: true, message: "Please enter total price" }]}
          >
            <Input placeholder="Please enter total price" />
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default Order;
