import React, { useEffect, useState } from "react";
import { Button, Form, Input, Table, Popconfirm, Modal } from "antd";
import "./order.css";
import { getOrder, addOrder, updateOrder, delOrder } from "../../api/index.js";

const Order = () => {
  // State Declarations
  const [listData, setListData] = useState({
    supplierName: "",
    materialName: "",
    page: 1,
    pageSize: 10,
  });
  const [tableData, setTableData] = useState([]);
  const [modalType, setModalType] = useState(0); // 0 for add, 1 for edit
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [form] = Form.useForm();

  // Effect Hook to Fetch Data
  useEffect(() => {
    console.log("Fetching data with listData:", listData);
    getTableData();
  }, [listData]);

  // Fetches table data from API based on current listData
  const getTableData = () => {
    getOrder(listData)
      .then(({ data }) => {
        setTableData(data.data.rows);
      })
      .catch((error) => {
        console.error("Error fetching table data:", error);
      });
  };

  // Handle search form submission
  const handleFinish = (values) => {
    console.log("Search submitted with values:", values);
    setListData({
      supplierName: values.supplierName || "",
      materialName: values.materialName || "",
      page: 1,
      pageSize: 10,
    });
  };

  // Opens modal for adding or editing an order
  const handleClick = (type, rowData) => {
    setIsModalOpen(true);
    setModalType(type === "add" ? 0 : 1);
    if (type === "edit") {
      form.setFieldsValue(rowData);
    }
  };

  // Handles form submission for adding or editing an order
  const handleOk = () => {
    form
      .validateFields()
      .then((values) => {
        if (modalType) {
          updateOrder(values).then(() => {
            handleCancel();
            getTableData();
          });
        } else {
          addOrder(values).then(() => {
            handleCancel();
            getTableData();
          });
        }
      })
      .catch((err) => {
        console.error("Validation failed:", err);
      });
  };

  // Closes the modal and resets form fields
  const handleCancel = () => {
    setIsModalOpen(false);
    form.resetFields();
  };

  // Deletes an order
  const handleDelete = (id) => {
    delOrder(id)
      .then(() => {
        getTableData();
      })
      .catch((error) => {
        console.error("Error deleting order:", error);
      });
  };

  // Handles pagination change
  const handlePageChange = (page, pageSize) => {
    setListData((prevState) => ({ ...prevState, page, pageSize }));
  };

  // Table Columns Definition
  const columns = [
    { title: "ID", dataIndex: "id" },
    { title: "Material Name", dataIndex: "materialName" },
    { title: "Supplier", dataIndex: "supplierName" },
    { title: "Material Quantity", dataIndex: "materialQuantity" },
    { title: "Single Price", dataIndex: "singlePrice" },
    { title: "Total Price", dataIndex: "totalPrice" },
    {
      title: "Action",
      render: (rowData) => (
        <div className="flex-box">
          <Button
            style={{ marginRight: "5px" }}
            onClick={() => handleClick("edit", rowData)}
          >
            Edit
          </Button>
          <Popconfirm
            title="Do you really want to delete this order?"
            okText="Confirm"
            cancelText="Cancel"
            onConfirm={() => handleDelete(rowData.id)}
          >
            <Button type="primary" danger>
              Delete
            </Button>
          </Popconfirm>
        </div>
      ),
    },
  ];

  // Rendering UI
  return (
    <div className="order">
      <div className="flex-box space-between">
        <Form layout="inline" onFinish={handleFinish}>
          <Form.Item name="materialName">
            <Input placeholder="Material Name" />
          </Form.Item>
          <Form.Item name="supplierName">
            <Input placeholder="Supplier Name" />
          </Form.Item>
          <Form.Item>
            <Button htmlType="submit" type="primary">
              Search
            </Button>
          </Form.Item>
        </Form>
        <Button type="primary" onClick={() => handleClick("add")}>
          + Add New Order
        </Button>
      </div>
      <Table
        style={{ marginTop: "20px" }}
        columns={columns}
        dataSource={tableData}
        rowKey={"id"}
        pagination={{
          pageSize: listData.pageSize,
          current: listData.page,
          total: tableData.length,
          onChange: handlePageChange,
        }}
      />
      <Modal
        open={isModalOpen}
        title={modalType ? "Edit Order" : "Add Order"}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form
          form={form}
          labelCol={{ span: 6 }}
          wrapperCol={{ span: 18 }}
          labelAlign="left"
        >
          {modalType === 1 && (
            <Form.Item name="id" hidden>
              <Input />
            </Form.Item>
          )}
          <Form.Item
            label="Supplier"
            name="supplierName"
            rules={[{ required: true, message: "Please enter supplier name" }]}
          >
            <Input placeholder="Please enter supplier name" />
          </Form.Item>
          <Form.Item
            label="Material Name"
            name="materialName"
            rules={[{ required: true, message: "Please enter material name" }]}
          >
            <Input placeholder="Please enter material name" />
          </Form.Item>
          <Form.Item
            label="Material Quantity"
            name="materialQuantity"
            rules={[
              { required: true, message: "Please enter material quantity" },
            ]}
          >
            <Input placeholder="Please enter material quantity" />
          </Form.Item>
          <Form.Item
            label="Single Price"
            name="singlePrice"
            rules={[{ required: true, message: "Please enter single price" }]}
          >
            <Input placeholder="Please enter single price" />
          </Form.Item>
          <Form.Item
            label="Total Price"
            name="totalPrice"
            rules={[{ required: true, message: "Please enter total price" }]}
          >
            <Input placeholder="Please enter total price" />
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default Order;
